    RecyclerView recyclerView;
    private List<Datum> list;
    private DetailFragment detailFragment;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        CoinLoreResponse coinLoreResponse =  new Gson().fromJson(data,CoinLoreResponse.class);
        recyclerView = findViewById(R.id.left);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
        list = coinLoreResponse.getData();
        FrameLayout frameLayout = findViewById(R.id.right);
        if (frameLayout!=null){
            Datum coin = list.get(0);
            FragmentTransaction fragmentTransaction = getSupportFragmentManager().beginTransaction();
            detailFragment = DetailFragment.getInstance(coin);
            fragmentTransaction.replace(R.id.right, detailFragment);
            fragmentTransaction.commitAllowingStateLoss();

        }
        CoinAdapter adapter = new CoinAdapter(list);
        recyclerView.setAdapter(adapter);
        adapter.setAdapterClickListener(new AdapterClickListener() {
            @Override
            public void onAdapterClick(int position) {
                Datum coin = list.get(position);
                if(detailFragment!=null){
                    detailFragment.changeCoin(coin);
                }else{
                    Intent intent = new Intent(MainActivity.this, DetailActivity.class);
                    intent.putExtra("detail",coin);
                    startActivity(intent);
                }
            }


        });



    }
